import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { MatSnackBar } from '@angular/material/snack-bar';
import { Route, Router } from '@angular/router';
import { AuthService } from '../services/auth/auth.service';

@Component({
  selector: 'app-signup',
  templateUrl: './signup.component.html',
  styleUrls: ['./signup.component.scss']
})

export class SignupComponent {

  signupForm!: FormGroup; //object for sign up form
  hidePassword=true; //variable for hide password

  constructor( private fb: FormBuilder,
    private snackBar: MatSnackBar,  //will show messages to the user
    private authService: AuthService,
    private router: Router){

    }

    ngOnInit(): void {  //controls for the sign up form
      this.signupForm=this.fb.group({   //fb-form builder
       name: [null, [Validators.required]],
       email: [null, [Validators.required, Validators.email]],
       password: [null, [Validators.required]],
       confirmpassword: [null, [Validators.required]],
      })
    }

    togglePasswordVisiblity(){
      this.hidePassword=!this.hidePassword;

    }

    onsubmit(): void {
      const password=this.signupForm.get('password')?.value;
      const confirmpassword=this.signupForm.get('confirmpassword')?.value;

      if(password !== confirmpassword){
        this.snackBar.open('Passwords do not match.', 'close', {duration:5000, panelClass: 'error-snackbar'});
        return;

      }

      this.authService.register(this.signupForm.value).subscribe(
        (response) =>{
          this.snackBar.open('Sign up successful!','Close', {duration: 5000});
          this.router.navigateByUrl("/login");

        },

        (error)=>{
          this.snackBar.open('Sign up failed. Please try again.', 'Close', {duration: 5000, panelClass: 'error-snackbar'});
        }
      )
}
}
















  <div class="container">
    <div class="welcome-message">
        <h1><span>ShopSasa!</span> <p></p>shop at the comfort <p></p>of your home</h1>
  </div>

    <mat-card>
        <mat-card-title>Sign up</mat-card-title>

        <mat-card-content>
            <form [formGroup]="signupForm">

                <mat-form-field appearance="outline">
                    <mat-label>Name</mat-label>
                    <input matInput formControlName="name" placeholder="Name" required>

                    <mat-error *ngIf="signupForm.get('name').invalid && signupForm.get('name').touched">
                    Please enter a valid name
                </mat-error>
                </mat-form-field>

                <mat-form-field appearance="outline">
                    <mat-label>Email</mat-label>
                    <input matInput formControlName="email" placeholder="Email" required>

                    <mat-error *ngIf="signupForm.get('email').invalid && signupForm.get('email').touched">
                    Please enter a valid email
                </mat-error>
                </mat-form-field>

                <mat-form-field appearance="outline">
                    <mat-label>Password</mat-label>
                    <input matInput type="{{hidePassword ? 'password' : 'text'}}" formControlName="password" placeholder="Password" required>

                    <button mat-icon-button matSuffix (click)="togglePasswordVisiblity()">
                        <mat-icon>{{ hidePassword ? 'visibility_off' : 'visibility' }}</mat-icon>
                    </button>

                    <mat-error *ngIf="signupForm.get('password').invalid && signupForm.get('password').touched">
                    Please enter a valid password
                </mat-error>
                </mat-form-field>

                <mat-form-field appearance="outline">
                    <mat-label>Confirm Password</mat-label>
                    <input matInput type="{{hidePassword ? 'password' : 'text'}}" formControlName="confirmpassword" placeholder="ConfirmPassword" required>

                    <button mat-icon-button matSuffix (click)="togglePasswordVisiblity()">
                        <mat-icon>{{ hidePassword ? 'visibility_off' : 'visibility' }}</mat-icon>
                    </button>

                    <mat-error *ngIf="signupForm.get('confirmpassword').invalid && signupForm.get('confirmpassword').touched">
                    Please enter a matching password
                </mat-error>
                </mat-form-field>

                <button mat-raised-button color="primary" type="submit" [disabled]="signupForm.invalid"
                (click)="onsubmit()">Sign Up</button>

                <!--on click of submit button a method on the ts file is called to create a new user in the backend API-->




            </form>
        </mat-card-content>
    </mat-card>
</div>
